mapper.select:
select id, nickname, account, status, avatar, email, phone, last_login_time, last_online_time, created_time, (select GROUP_CONCAT(label SEPARATOR ',') from horizon_role tr where exists(select 1 from horizon_user_role tur where tur.id_horizon_role = tr.id and tur.id_horizon_user = tu.id)) as role_names from horizon_user tu where tu.del_flag = 1
and account = #{account}
and email = #{email}
and (instr(account, #{query}) > 0 or instr(nickname, #{query}) > 0)
and created_time between str_to_date(#{startDate}, '%Y-%m-%d') and str_to_date(#{endDate}, '%Y-%m-%d') + interval 1 day
order by #{sort}
#{order}
